import { isValidStats, Stats } from "./stats";

export class PlayerProfile {
  public name: string = "";
  public imageURL: string =
    "data:image/png;base64,";
  public stats!: Stats;
  constructor(init: PlayerProfile) {
    if (!init) {
      return;
    }
    isValidStats(init.stats);
    Object.assign(this, init);
  }
}
